INSERT INTO `news` (`NewsId`, `Title`, `Content`, `Image`, `Keywords`, `MetaDescription`, `User`, `Status`, `Date`, `Summary`) VALUES
(2, 'قیمت و طرح‌های جدید اینترنت نامحدود', '<span style=\"font-family: tahoma,arial,helvetica,sans-serif; font-size: 10pt;\">نرخ های جدید بر مبنای ۶ مدل سرعت اینترنت درنظر گرفته شده است که با توجه به میزان مصرف کاربران ADSL هزینه ماهانه اینترنت برای کاربران کاهش یافته است اما &nbsp;محدودیت ارائه تکنولوژی نیز برداشته شده است و اپراتورها می توانند متناسب با تقاضای سرعت کاربر به آنها اینترنت ADSL ، فیبرنوری و VDSL ارئه بدهند تا کاربران کیفیت بیشتری را تجربه بکنند.</span> &nbsp;\r\n&nbsp;', '/News//inter.png', 'تست', 'تست', 1, 1, '2017/07/31', 'نرخ های جدید بر مبنای ۶ مدل سرعت اینترنت درنظر گرفته شده است که با توجه به میزان مصرف کاربران ADSL هزینه ماهانه اینترنت برای کاربران کاهش یافته است اما  محدودیت ارائه تکنولوژی نیز برداشته شده است'),
(3, ' خداحافظی با یکی از جنجالی‌ترین نرم‌افزارهای تاریخ ', '<span style=\"font-family: tahoma, arial, helvetica, sans-serif; font-size: 10pt;\">این نرم&zwnj;افزار با نرم&zwnj;افزارهای استاندارد دیگری نظیر HTML5 جایگزین خواهد شد که نه&zwnj;تنها تمام ویژگی&zwnj;ها و قابلیت&zwnj;های مربوط به فلش را به کاربر ارائه می&zwnj;دهد، بلکه برای استفاده از این قابلیت&zwnj;ها نیازی به نصب پلاگین خاصی نیز وجود نخواهد داشت.</span><br /><br /><span style=\"font-family: tahoma, arial, helvetica, sans-serif; font-size: 10pt;\"> شرکت ادوبی برای تشویق کمپانی&zwnj;های بزرگ به حرکت به&zwnj;سوی این نرم&zwnj;افزار جدید، با کمپانی&zwnj;های بزرگ مانند اپل، فیس&zwnj;بوک و گوگل همکاری خواهد کرد. این کمپانی&zwnj;ها نیز پشتیبانی خود از ادوبی را به&zwnj;صورت جداگانه ابراز داشته&zwnj;اند.</span>', '/News//flash.png', 'شرکت ,ادوبی,, اعلام کرده است که سرانجام, فلش, پلیر, به پایان راه خود رسیده است و این کمپانی پس از سال 2020 میلادی دیگر به پشتیبانی از این سیستم ادامه نخواهد داد', 'شرکت ادوبی اعلام کرده است که سرانجام فلش پلیر به پایان راه خود رسیده است و این کمپانی پس از سال 2020 میلادی دیگر به پشتیبانی از این سیستم ادامه نخواهد داد', 1, 1, '2017/08/01', 'شرکت ادوبی اعلام کرده است که سرانجام فلش پلیر به پایان راه خود رسیده است و این کمپانی پس از سال 2020 میلادی دیگر به پشتیبانی از این سیستم ادامه نخواهد داد'),
(4, ' رایگان شدن آنتی ویروس کسپرسکی ', '<span style=\"font-family: tahoma, arial, helvetica, sans-serif; font-size: 10pt;\">شرکت امنیتی کسپرسکی، می&zwnj;خواهد نرم افزار آنتی ویروس خود را در ایالات متحده و سپس در انگلستان به صورت رایگان ارائه دهد.</span>\r\n<span style=\"font-family: tahoma, arial, helvetica, sans-serif; font-size: 10pt;\">هر چند ارائه رایگان این آنتی ویروس می&zwnj;تواند یک پیشنهاد خیرخواهانه تلقی گردد،&zwnj;اما از طرفی می&zwnj;توان به این موضوع، به چشم دامی از سوی کشور روسیه و با هدف دسترسی به اطلاعات آمریکایی&zwnj;ها و سوءاستفاده از اطلاعات مورد بحث نگاه کرد.</span>', '/News//kasper.png', '', '', 1, 1, '2017/08/01', 'شرکت امنیتی کسپرسکی، می‌خواهد نرم افزار آنتی ویروس خود را در ایالات متحده و سپس در انگلستان به صورت رایگان ارائه دهد.'),
(5, 'واکنش تلگرام به خبر انتقال سرورهایش به ایران ', '<span style=\"font-family: tahoma, arial, helvetica, sans-serif; font-size: 10pt;\">&nbsp;انتقال سرورهای تلگرام به ایران، موضوعی است که این اواخر بارها اعلام و پس از چند ساعت، توسط مدیر تلگرام تکذیب شده است. امروز نیز وزیر ارتباطات و فناوری اطلاعات مجددا اعلام کرد که جلسات فشرده&zwnj;ای با این شرکت برگزار شده و سرورهای این سرویس پیام&zwnj;رسان به ایران منتقل شده است. واعظی در سخنانش اظهار داشت که این توافق یک&zwnj;طرفه نیست و مذاکرات نتیجه داده&zwnj;اند. به دنبال انتشار این خبر، پاول دورف امروز مجددا در صفحه&zwnj;ی توییتر رسمی خود، پاسخگوی کاربران بود و به آن&zwnj;ها اعلام کرد که سرورهای تلگرام هرگز به ایران منتقل نخواهند شد</span>', '/News//teleg.png', ' انتقال سرورهای تلگرام به ایران، موضوعی است که این اواخر بارها اعلام و پس از چند ساعت، توسط مدیر تلگرام تکذیب شده است. امروز نیز وزیر ارتباطات و فناوری اطلاعات مجددا اعلام کرد که جلسات فشرده‌ای با این شرکت برگزار شده و سرورهای این سرویس پیام‌رسان به ایران منتقل شده است. واعظی در سخنانش اظهار داشت که این توافق یک‌طرفه نیست و مذاکرات نتیجه داده‌اند. به دنبال انتشار این خبر، پاول دورف امروز مجددا در صفحه‌ی توییتر رسمی خود، پاسخگوی کاربران بود و به آن‌ها اعلام کرد که سرورهای تلگرام هرگز به ایران منت', ' انتقال سرورهای تلگرام به ایران، موضوعی است که این اواخر بارها اعلام و پس از چند ساعت، توسط مدیر تلگرام تکذیب شده است. امروز نیز وزیر ارتباطات و فناوری اطلاعات مجددا اعلام کرد که جلسات فشرده‌ای با این شرکت برگزار شده و سرورهای این سرویس پیام‌رسان به ایران منتقل شده است. واعظی در سخنانش اظهار داشت که این توافق یک‌طرفه نیست و مذاکرات نتیجه داده‌اند. به دنبال انتشار این خبر، پاول دورف امروز مجددا در صفحه‌ی توییتر رسمی خود، پاسخگوی کاربران بود و به آن‌ها اعلام کرد که سرورهای تلگرام هرگز به ایران منت', 1, 1, '2017/08/01', ' انتقال سرورهای تلگرام به ایران، موضوعی است که این اواخر بارها اعلام و پس از چند ساعت، توسط مدیر تلگرام تکذیب شده است. امروز نیز وزیر ارتباطات و فناوری اطلاعات مجددا اعلام کرد که جلسات فشرده‌ای با این شرکت برگزار شده و سرورهای این سرویس پیام‌رسان به ایران منتقل شده است. واعظی در سخنانش اظهار داشت که این توافق یک‌طرفه نیست و مذاکرات نتیجه داده‌اند. به دنبال انتشار این خبر، پاول دورف امروز مجددا در صفحه‌ی توییتر رسمی خود، پاسخگوی کاربران بود و به آن‌ها اعلام کرد که سرورهای تلگرام هرگز به ایران منت'),
(6, ' چین بزرگ‌ترین جهان مجازی را خلق کرد ', '<span style=\"font-family: tahoma, arial, helvetica, sans-serif; font-size: 10pt;\">گروهی از دانشمندان چینی با کمک سریع&zwnj;ترین ابر رایانه دنیا بزرگ&zwnj;ترین جهان مجازی را ایجاد کردند و آن را &laquo;تمرین اولیه&raquo; ماشین Sunway TaihuLight نام&zwnj;گذاری کردند. این ابررایانه با استفاده از ۱۰ میلیون هسته CPU این شبیه سازی را انجام داده است.</span><br /><br /><span style=\"font-family: tahoma, arial, helvetica, sans-serif; font-size: 10pt;\">به هرحال این ماشین خلق و گسترش اولیه جهان در میلیون&zwnj;ها سال قبل را شبیه سازی کرد. جهان مجازی ساخته شده ۵ برابر شبیه سازی محققان دانشگاه زوریخ است که در ماه ژوئن رونمایی شد.</span><br /><br /><span style=\"font-family: tahoma, arial, helvetica, sans-serif; font-size: 10pt;\">شبیه سازی جهان به ستاره شناسان کمک می&zwnj;کند در مناطق مختلف فضا جستجوهایی انجام دهند.</span><br /><br /><span style=\"font-family: tahoma, arial, helvetica, sans-serif; font-size: 10pt;\"> نسل آتی ابررایانه ها ۱۰ برابر قدرتمندتر از Sunway TaihuLight خواهند بود که در ۲۰۱۹ میلادی آماده می شوند</span>', '/News//majazi.png', 'گروهی از دانشمندان چینی با کمک سریع‌ترین ابر رایانه دنیا بزرگ‌ترین جهان مجازی را ایجاد کردند و آن را «تمرین اولیه» ماشین Sunway TaihuLight نام‌گذاری کردند. این ابررایانه با استفاده از ۱۰ میلیون هسته CPU این شبیه سازی را انجام داده است.  به هرحال این ماشین خلق و گسترش اولیه جهان در میلیون‌ها سال قبل را شبیه سازی کرد. جهان مجازی ساخته شده ۵ برابر شبیه سازی محققان دانشگاه زوریخ است که در ماه ژوئن رونمایی شد.  شبیه سازی جهان به ستاره شناسان کمک می‌کند در مناطق مختلف فضا جستجوهایی انجام دهند.  نسل آت', 'گروهی از دانشمندان چینی با کمک سریع‌ترین ابر رایانه دنیا بزرگ‌ترین جهان مجازی را ایجاد کردند و آن را «تمرین اولیه» ماشین Sunway TaihuLight نام‌گذاری کردند. این ابررایانه با استفاده از ۱۰ میلیون هسته CPU این شبیه سازی را انجام داده است.\r\n\r\nبه هرحال این ماشین خلق و گسترش اولیه جهان در میلیون‌ها سال قبل را شبیه سازی کرد. جهان مجازی ساخته شده ۵ برابر شبیه سازی محققان دانشگاه زوریخ است که در ماه ژوئن رونمایی شد.\r\n\r\nشبیه سازی جهان به ستاره شناسان کمک می‌کند در مناطق مختلف فضا جستجوهایی انجام دهند.\r\n\r\n', 1, 1, '2017/08/01', 'گروهی از دانشمندان چینی با کمک سریع‌ترین ابر رایانه دنیا بزرگ‌ترین جهان مجازی را ایجاد کردند و آن را «تمرین اولیه» ماشین Sunway TaihuLight نام‌گذاری کردند. این ابررایانه با استفاده از ۱۰ میلیون هسته CPU این شبیه سازی را انجام داده است.\r\n\r\nبه هرحال این ماشین خلق و گسترش اولیه جهان در میلیون‌ها سال قبل را شبیه سازی کرد. جهان مجازی ساخته شده ۵ برابر شبیه سازی محققان دانشگاه زوریخ است که در ماه ژوئن رونمایی شد.\r\n\r\nشبیه سازی جهان به ستاره شناسان کمک می‌کند در مناطق مختلف فضا جستجوهایی انجام دهند.\r\n\r\n'),
(7, 'جایزه 60ملیون تومانی', '<p style=\"box-sizing: inherit; outline: none 0px; margin-top: 0px; line-height: 20px; margin-bottom: 1rem; font-size: 1.1em; font-family: iranyekan, iranyekanNum, sans-serif; text-align: justify; color: #292b2c; word-spacing: 1px;\"><span style=\"font-family: tahoma, arial, helvetica, sans-serif; font-size: 10pt;\">وب سایت \"<a style=\"box-sizing: inherit; outline: none 0px; background-color: transparent; color: #333333; text-decoration-line: none; touch-action: manipulation; cursor: pointer; -webkit-tap-highlight-color: transparent; font-size: 1.1em; line-height: 29.4272px;\" href=\"www.lastsecond.ir/\" target=\"_blank\" rel=\"noopener\"><span style=\"box-sizing: inherit; outline: none 0px; font-weight: bolder; line-height: 32.3699px;\">لست سکند</span></a>\" با همکاری آژانس های مسافرتی عضو و در جهت ترویج فرهنگ سفر و به اشتراک گذاری دانش و تجربه کاربران خود اقدام به راه اندازی مسابقه ای با عنوان \"<span style=\"box-sizing: inherit; outline: none 0px; font-weight: bolder; line-height: 29.4272px;\">مسابقه بزرگ سفرنامه نویسی</span>\" برای ششمین&nbsp;سال متوالی نموده است (اگر نمی دانید سفرنامه چیست<a style=\"box-sizing: inherit; outline: none 0px; background-color: transparent; color: #333333; text-decoration-line: none; touch-action: manipulation; cursor: pointer; -webkit-tap-highlight-color: transparent; font-size: 1.1em; line-height: 29.4272px;\" href=\"www.lastsecond.ir/staticPages/itineraries/\" target=\"_blank\" rel=\"noopener\"><span style=\"box-sizing: inherit; outline: none 0px; font-weight: bolder; line-height: 32.3699px;\">&nbsp;اینجا کلیک</span></a>&nbsp;کنید |&nbsp;<a style=\"box-sizing: inherit; outline: none 0px; background-color: transparent; color: #333333; text-decoration-line: none; touch-action: manipulation; cursor: pointer; -webkit-tap-highlight-color: transparent; font-size: 1.1em; line-height: 29.4272px;\" href=\"fa.wikipedia.org/wiki/%D8%B3%D9%81%D8%B1%D9%86%D8%A7%D9%85%D9%87\" target=\"_blank\" rel=\"noopener\">توض